mapper.insert:
insert into board5(
	b_id, b_name, b_title, b_content, b_pwd, b_date, b_hit		
)values(
	mission.NEXTVAL, #{b_name}, #{b_title}, #{b_content}, #{b_pwd}, SYSDATE, 0
)

mapper.update:
update board5 set
	b_hit = b_hit+1
	where b_id = #{b_id}

update board5 set
	b_title = #{b_title}, b_content = #{b_content}	
	where b_id=#{b_id}
